    void rec::fmtC(struct fmt* f, uint64_t c)
    {
        auto r = gocpp::rune(c);
        if(c > utf8::MaxRune)
        {
            r = utf8::RuneError;
        }
        auto buf = f->intbuf.make_slice(0, 0);
        rec::pad(gocpp::recv(f), utf8::AppendRune(buf, r));
    }

