            print("pacer: assist ratio=", assistRatio, " (scan ", rec::Load(gocpp::recv(gcController.heapScan)) >> 20, " MB in ", work.initialHeapLive >> 20, "->", heapGoal >> 20, " MB)", " workers=", dedicatedMarkWorkersNeeded, "+", c->fractionalUtilizationGoal, "\n");
