        {
            println("runtime: failed mSpanList.insert", span, span->next, span->prev, span->list);
            go_throw("mSpanList.insert");
        }
        span->next = list->first;
        if(list->first != nullptr)
        {
            list->first->prev = span;
        }
        else
        {
            list->last = span;
        }
        list->first = span;
        span->list = list;
