    Time Unix(int64_t sec, int64_t nsec)
    {
        if(nsec < 0 || nsec >= 1e9)
        {
            auto n = nsec / 1e9;
            sec += n;
            nsec -= n * 1e9;
            if(nsec < 0)
            {
                nsec += 1e9;
                sec--;
            }
        }
        return unixTime(sec, int32_t(nsec));
    }

