    bool isLeap(int year)
    {
        return year % 4 == 0 && (year % 100 != 0 || year % 400 == 0);
    }

    std::tuple<int, int> norm(int hi, int lo, int base)
    {
        int nhi;
        int nlo;
        if(lo < 0)
        {
            int nhi;
            int nlo;
            auto n = (- lo - 1) / base + 1;
            hi -= n;
            lo += n * base;
        }
        if(lo >= base)
        {
            int nhi;
            int nlo;
            auto n = lo / base;
            hi += n;
            lo -= n * base;
        }
        return {hi, lo};
    }

